import axios from "axios";
import { useEffect, useState } from "react";
import styled from "styled-components";
import Input from "../styledComponents/Inputs";
import Footer from "./Footer";
import Header from "./Header";

export default function Main() {
  const [petArr, setPetArr] = useState([]);
  const [change, setChange] = useState(false)
  const URL = "http://localhost:4000/pet/get";
  useEffect(() => {
    axios
      .get(URL)
      .then((response) => {
        const { data } = response;
        console.log(data);
        setPetArr(data);
      })
      .catch((err) => console.log(err.response));
  }, []);

  if(petArr.length>0)return(
    <Style>
      <Header />
      <p>to no array {petArr.length} </p> {console.log(petArr)}
      {petArr.map((pet)=>{return <Input><input>{pet.name}</input></Input>})}
      <Footer />
    </Style>
  )
  else return (
    <Style><button onClick={()=>setChange(!change)}>cadastre um nen√©m</button></Style>
  );
}

const Style = styled.div`
display: flex;
margin-top: 100px;
`
