import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import styled from "styled-components";
import Footer from "./Footer";
import Header from "./Header";
import ModalDate from "./ModalDate";
import logoDog from "../assets/imgs/dog-logo.png";
import logoCat from "../assets/imgs/cat-logo.png"


export default function PetInfo() {
  const [petInfo, setPetInfo] = useState([]);
  const [date, setDate] = useState("");
  const [modal, setModal] = useState(false);
  const token =localStorage.getItem("token")
  const [petHealth,setPetHealth] = useState([])
  const [toggleInput, setToggleInput] = useState(false)
  const [vaccine,setVaccine] = useState('')

  const { idPet } = useParams();
  const {idNature} = useParams(); 
  const URL = `http://localhost:4000/pet/get/${idNature}/${idPet}`;
  const URL_DATE = `http://localhost:4000/pet/health-${idNature}/patch/${vaccine}/${idPet}`
  const URL_HEALTH =  `http://localhost:4000/pet/health-${idNature}/post/${idPet}`
  const URL_HEALTH_GET =  `http://localhost:4000/pet/health-${idNature}/get/${idPet}`

  useEffect(() => {
    axios
      .get(URL)
      .then((response) => {
        const { data } = response;
        setPetInfo([data]);
        setPetHealth([data[0].Dog])
        console.log("get pet ", data);
      })
      .catch((error) => {
        console.log(error.response); 
      });
  }, []);

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
//   useEffect(()=>{ axios.get(URL_HEALTH_GET ).then(response=>{
//     setPett(response.data)
//     console.log("get health",response.data)
// })},[])

  // if(petHealth.length==0){
  //   axios.post(URL_HEALTH,{}, config)
  // }

  if (petInfo.length > 0) {
    const petArr = petInfo[0];
    const pet = petArr[0]
    console.log("pet data", petInfo)
